const mongoose = require("mongoose");

const userSchema = mongoose.Schema({
	name: {
		type: String,
		required: true,
		min: 6
	},
	email: {
		type: String,
		required: true
	},
	password: {
		type: String,
		required: true,
		min: 6
	},
	image: {
		type: String,
		default:
			"data:image/jpeg;base64,"
	},
	date: {
		type: Date,
		default: Date.now
	}
});

module.exports = mongoose.model("User", userSchema);
